mapper.select:
SELECT * FROM (
	SELECT rownum rnum, B.* FROM (
		SELECT
			BOARD_NO, BOARD_TITLE, BOARD_CONTENT, BOARD_DATE, 
			CATEGORY_TYPE, BOARD_HIT, BOARD_LIKE, USER_NO, USER_ID, USER_NICK
		FROM board
		ORDER BY board_no DESC
	) B
) BOARD
WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE board
SET board_hit = board_hit + 1
WHERE board_no = #{boardNo}
