mapper.select:
SELECT * FROM (
	SELECT rownum rnum, B.* FROM (
		SELECT
			board_no, board_title, board_content
			board_date, category_type, board_hit
		FROM board
		ORDER BY board_no DESC
	) B
) BOARD
WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
INSERT board
SET hit = hit + 1
WHERE board_no = #{boardNo}

mapper.select:
SELECT board_no, board_title, board_content, user_id, user_nick, board_hit, board_date
FROM board
WHERE board_no = #{boardNo}
