mapper.select:
SELECT * FROM (
	SELECT rownum rnum, B.* FROM (
		SELECT * FROM board
		LEFT OUTER JOIN users
		ON board.user_no = users.user_no
		ORDER BY board_no DESC
	) B
) BOARD
WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE board
SET board_hit = board_hit + 1
WHERE board_no = #{boardNo}
